            # Move to the next frame
            frame += 1
            i += 1  # Move to the next roll
        # Check for a spare
        elif bowling_card[i] == '/':
            # Calculate spare score: 10 minus previous roll plus next roll
            total_score += 10 - roll_value(bowling_card[i - 1])
            if i + 1 < len(bowling_card):
                total_score += roll_value(bowling_card[i + 1])
            # Move to the next frame
            frame += 1
            i += 1  # Move to the next roll
        else:
            # Add value of the roll to the score
            total_score += roll_value(bowling_card[i])
