def my_func(s):
    if s is None:
        return None

    result = []
    capitalize_next = False

    for char in s:
        if char == '-':
            capitalize_next = True
        elif capitalize_next:
            result.append(char.upper())
            capitalize_next = False
        else:
            result.append(char)

    return ''.join(result)

