def my_func(s: str) -> str:
    if '-' not in s:
        return s
    parts = s.split('-')
    for i in range(1, len(parts)):
        if parts[i]:
            parts[i] = parts[i][0].upper() + parts[i][1:]
    return ''.join(parts)

def my_func(s):
    parts = s.split()
    result = []
    for part in parts:
        words = part.split('-')
        camel_case = words[0] + ''.join(word.capitalize() for word in words[1:])
        result.append(camel_case)
    return ' '.join(result)
