def my_func(s):
    parts = s.split(' ')
    for i in range(len(parts)):
        sub_parts = parts[i].split('-')
        if len(sub_parts) > 1:
            sub_parts = [sub_parts[0]] + [x.capitalize() for x in sub_parts[1:]]
        parts[i] = ''.join(sub_parts)
    return ' '.join(parts)
