def my_func(input_str):
    if input_str is None:
        return None

    result = []
    capitalize_next = False

    for char in input_str:
        if char == '-':
            capitalize_next = True
        elif capitalize_next:
            result.append(char.upper())
            capitalize_next = False
        else:
            result.append(char)

    return ''.join(result)

def my_func(s: str) -> str:
    if '-' not in s:
        return s

    parts = s.split(' ')
    for i, part in enumerate(parts):
        if '-' in part:
            sub_parts = part.split('-')
            sub_parts = [sub_parts[0]] + [sub.capitalize() for sub in sub_parts[1:]]
            parts[i] = ''.join(sub_parts)
    return ' '.join(parts)

