def my_func(s):
    parts = s.split(' ')
    for i in range(len(parts)):
        if '-' in parts[i]:
            parts[i] = ''.join(word.capitalize() if idx != 0 else word for idx, word in enumerate(parts[i].split('-')))
    return ' '.join(parts)

def my_func(*inputs):
    outputs = []
    for input_str in inputs:
        if not input_str:  # Handle the case of None or empty string
            outputs.append(input_str)
            continue
        result = []
        capitalize_next = False
        for char in input_str:
            if char == '-':
                capitalize_next = True
            elif char == ' ':
                result.append(char)
                capitalize_next = False  # Reset for spaces
            else:
                if capitalize_next:
                    result.append(char.upper())
                    capitalize_next = False
                else:
                    result.append(char)
        outputs.append(''.join(result))
    return tuple(outputs)

