def my_func(input_str):
    if input_str is None:
        return None

    # Split the string by spaces
    parts = input_str.split(' ')
    result = []

    for part in parts:
        # Split each word by '-' and capitalize each part except the first one
        sub_parts = part.split('-')
        if len(sub_parts) > 1:
            camel_case_word = sub_parts[0] + ''.join(sub.capitalize() for sub in sub_parts[1:])
        else:
            camel_case_word = part  # No '-' means no transformation

        result.append(camel_case_word)

    # Join the transformed parts back with spaces
    return ' '.join(result)

