def my_func(input_string):
    if input_string is None:
        return None
    words = input_string.split()
    result = []
    for word in words:
        parts = word.split('-')
        transformed = parts[0] + ''.join(part.capitalize() for part in parts[1:])
        result.append(transformed)
    return ' '.join(result)

def my_func(s):
    def convert_segment(segment):
        if '-' in segment:
            words = segment.split('-')
            return ''.join(word.capitalize() for word in words)
        return segment

    segments = s.split(' ')
    return ' '.join(convert_segment(segment) for segment in segments)

def my_func(input1):
    if not input1 or len(input1) > 20:
        return input1

    result = []
    capitalize_next = False

    for char in input1:
        if char == '-':
            capitalize_next = True
        elif capitalize_next:
            result.append(char.upper())
            capitalize_next = False
        else:
            result.append(char)

    return ''.join(result)

