def my_func(s):
    if s is None:
        return None

    result = []
    capitalize_next = False

    for char in s:
        if char == '-':
            capitalize_next = True
        else:
            if capitalize_next:
                result.append(char.upper())
                capitalize_next = False
            else:
                result.append(char)

    return ''.join(result)

def my_func(s):
    if s is None:
        return None
    words = s.split()
    result = []
    for word in words:
        parts = word.split('-')
        camel_case_word = parts[0] + ''.join(part.capitalize() for part in parts[1:])
        result.append(camel_case_word)
    return ' '.join(result)

